sp_widget.client_script:
api.controller=function() {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectNote', function(event, note) { 
		console.log('Listener caught NoteID: ' + note); 
		c.data.noteTitle = note.title;
		c.data.noteDescription = note.note;
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			console.log('Promise returned: ' + r.data.note.title);
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		})
	});

	c.updateNote = function(updateField){
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then(function(r) {
		});

		$rootScope.$emit('updateList', c.data);
	}



};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	/*  if (input && input.noteID) {
      var note = new GlideRecord('x_snc_createnotes_note');
      if (note.get(input.noteID)) {
        if (input.action == 'getNote') {
          data.note = {};
          $sp.getRecordValues(data.note, note, "title, note, sys_id");
        }
      }
    }
	*/
	if (input && input.noteID) {
		var grNote = new GlideRecord('x_snc_createnotes_note');
		if (grNote.get(input.noteID)) {
			if(input.action == 'getNote'){
				data.note = {};
				$sp.getRecordValues(data.note, grNote, "title, note, sys_id");
			}else if (input.action == 'updateNote') { 
				grNote.title = input.noteTitle; 
				grNote.note = input.noteBody; 
				grNote.update(); 
			}
		}
	}

})();
