sp_widget.client_script:
api.controller=function() {
	/* widget controller */
	var c = this;

	for(var property in c.data){
		console.log('property: ' + c.data[property]);
	}

	for(var note in c.data.notes){
		console.log('note: ' + c.data.notes[note].title);
	}

	c.selectItem = function(index){
		var noteID = c.data.notes[index].sys_id;
		var note = c.data.notes[index];
		$rootScope.noteID = noteID;
		console.log('noteID: ' + noteID);
		$rootScope.$emit('selectNote', note);
	}

	$rootScope.$on('updateList', function(event, note) {
		console.log('List - Note title: ' + note.title); 
		console.log('List - Note desc: ' + note.note);
		console.log('List - Note sys: ' + note.noteID);

		for(var index in c.data.notes){
			if(c.data.notes[index].sys_id === note.noteID){
				console.log("Index element to update: " + index);
				c.data.notes[index].title = note.title;
				c.data.notes[index].note = note.note;
			}

		}

	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.notes = [];

	var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user', gs.getUserID());
	gr.orderByDesc('sys_created_on');
	gr.query();
	var noteObj = {};

	while(gr.next()){
		noteObj = {};
		//number, title, and sys_id
		$sp.getRecordDisplayValues(noteObj, gr, 'number,title,sys_id');
		noteObj.note = gr.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}


})();
